mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT cbo_type, cbo_num, mem_num,
REPLACE(REPLACE(cbo_title,'<','&lt;'),'>','&gt;') cbo_title,
cbo_content, cbo_hit, cbo_rdate, mem_id, re_cnt, fav_cnt FROM cboard LEFT OUTER JOIN (SELECT COUNT(*) re_cnt, cbo_num FROM cboard_re GROUP BY cbo_num) USING(cbo_num) LEFT OUTER JOIN (SELECT COUNT(*) fav_cnt, cbo_num FROM cboard_fav GROUP BY cbo_num) USING(cbo_num) JOIN member USING (mem_num)
)a)
WHERE rnum >= #{start} AND rnum <= #{end}

mapper.update:
UPDATE cboard SET
	cbo_type = #{cbo_type},
	cbo_title = #{cbo_title},
	cbo_content = #{cbo_content}
WHERE cbo_num = #{cbo_num}
