mapper.select:
		SELECT c.con_num, c.con_type, c.con_title, c.con_content, c.con_rdate, c.con_mdate, m.*, r.con_re_cnt
		FROM consulting c
		LEFT JOIN (select *
					from member
					left join member_detail
					using (mem_num)
					) m on c.mem_num = m.mem_num
		LEFT JOIN (
                select con_num, count(*) as con_re_cnt
                from con_reply
                group by con_num
                )r on r.con_num = c.con_num
		WHERE c.con_num = #{con_num}

mapper.insert:
INSERT INTO con_reply (con_re_num,
						con_num,
						doc_num,
						con_re_content
						)
VALUES (con_reply_seq.nextval,
		#{con_num},
		#{doc_num},
		#{con_re_content}
		)

mapper.select:
SELECT count(*) 
FROM con_reply 
WHERE con_num=#{con_num}
